INSERT INTO films(name, description, year, genre_id, minimal_age, duration_in_minutes, file_id)
VALUES('Мечты Робота', 'Нью-Йорк, 1980-е годы. Друзья Робот и Пёс отправляются на Лонг-Айленд. Пёс отправляется в город за спасателями, пытаясь заполнить пустоту внутри. Робот, тем временем, пускается в яркие сновидения, вспоминая о проведённом с Псом времени.', 2023, 5, 6, 169, 2);
INSERT INTO films(name, description, year, genre_id, minimal_age, duration_in_minutes, file_id)
VALUES('Все страхи Бо', 'Бо боится всего на свете и старается не выходить из дома. Однажды ему придётся навестить мать в родном городе. Поездка оборачивается увлекательным кошмаром, выходящим за рамки времени и пространства.', 2023, 2, 18, 179, 4);
INSERT INTO films(name, description, year, genre_id, minimal_age, duration_in_minutes, file_id)
VALUES('Оставленные', 'Рождество 1970 года, штат Массачусетс. В престижной школьной академии на праздники остаются ненавидимый всеми учитель Пол, 15-летний проблемный ученик Ангус и повариха Мэри, чей сын погиб во Вьетнаме. Троица формирует необычную связь и открывает себя и друг друга с новых сторон.', 2023, 2, 17, 117, 3);
INSERT INTO films(name, description, year, genre_id, minimal_age, duration_in_minutes, file_id)
VALUES('Барби', 'Маленькую куколку за несовершенство изгоняют из мира «Барбилэнд», после чего она вынуждена отправиться в путешествие в реальный мир.', 2023, 1, 13, 180, 1);