INSERT INTO halls(name, row_count, place_count, description) VALUES ('RED', 14, 22, 'Зал RED в кинотеатре — это современное пространство с передовыми технологиями, включая 4K проекторы и звуковую систему Dolby Atmos, что обеспечивает максимальное погружение в фильм. Комфортабельные сиденья с возможностью принять положение лежа и стильный интерьер создают атмосферу уюта.');
INSERT INTO halls(name, row_count, place_count, description) VALUES ('BLUE', 10, 15, 'Зал BLUE в кинотеатре предлагает уникальный опыт просмотра фильмов с акцентом на комфорт и атмосферу. Оснащенный современными проекторами и качественной звуковой системой, он обеспечивает отличное изображение и звук. Интерьер зала оформлен в спокойных синих тонах, создающих расслабляющую обстановку. Удобные кресла и возможность заказать закуски делают просмотр еще более приятным и комфортным.');
INSERT INTO halls(name, row_count, place_count, description) VALUES ('GREEN', 8, 10, 'Зал GREEN в нашем кинотеатре - это уникальное пространство, созданное для любителей кино, которые ценят не только качество изображения и звука, но и комфорт и экологичность. Интерьер зала выполнен в успокаивающих зеленых тонах, что создает атмосферу гармонии и единения с природой.');